const initialState = {
  isLoggedIn: false,
  user: {},
}

const LOG_IN = "LOG_IN"; //액숀의 이름
const LOG_OUT = "LOG_OUT"; //액숀의 이름

const loginAction = {
  type: LOG_IN,
  data: {
    nickname: "제로초",
  }
}

const logoutAction = {
  type: LOG_OUT,
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN:
      return {
        ...state,
        isLoggedIn: true,
        user: action.data,
      }
    case LOG_OUT:
      return {
        ...state,
        isLoggedIn: true,
        user: action.data,
      }
    default : 
      return {
        ...state,
      }
  }
};

export default reducer;
