import React from 'react';
import PropTypes from 'prop-types';
import { Icon } from 'antd';

const PostImages = ({ images }) => {
    if(images.length === 1) {
        return (
            <img src={`http://localhost:3065/${images[0].src}`}></img>
        );
    }
    if( images.length === 2) {
        return (
            <div>
                <img src={`http://localhost:3065/${images[0].src}`} width="50%"></img>
                <img src={`http://localhost:3065/${images[1].src}`} width="50%"></img>
            </div>
        );
    }
    return (
        <div>
            <img src={`http://localhost:3065/${images[0].src}`} width="50%"></img>
            <div style={{ display: 'inline-block', width: '50%', textAlign: 'center', verticalAlign: 'middle' }} onClick={onZoom}>
        <Icon type="plus" />
        <br />
        {images.length - 1}
        개의 사진 더보기
      </div>
        </div>
    );
};

PostImages.propTypes = {
    images : PropTypes.arrayOf(PropTypes.shape({
        src:PropTypes.string,
    })).isRequired,
};

export default PostImages;
