import {
  all,
  fork,
  takeLatest,
  takeEvery,
  call,
  put,
  take,
  delay
} from "redux-saga/effects";
import axios from "axios";
import {
  LOAD_USER_FAILURE,
  LOAD_USER_REQUEST,
  LOAD_USER_SUCCESS,
  LOG_IN_FAILURE,
  LOG_IN_REQUEST,
  LOG_IN_SUCCESS,
  LOG_OUT_FAILURE,
  LOG_OUT_REQUEST,
  LOG_OUT_SUCCESS,
  SIGN_UP_FAILURE,
  SIGN_UP_REQUEST,
  SIGN_UP_SUCCESS
} from "../reducers/user";

axios.defaults.baseURL = "http://localhost:8080/api";

//로그인
function* logIn(action) {
  try {
    const result = yield call(logInAPI, action.data); //서버 응답
    yield put({
      // put은 dispatch 동일
      type: LOG_IN_SUCCESS,
      data: result.data
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: LOG_IN_FAILURE
    });
  }
}

//로그인 API
function logInAPI(logInData) {
  //서버에 요청
  return axios.post("/user/login", logInData, {
    withCredentials: true
  });
}

//로그인 watch
function* watchLogIn() {
  yield takeEvery(LOG_IN_REQUEST, logIn);
}

//회원가입
function* signUp(action) {
  try {
    yield call(signUpAPI, action.data);
    yield put({
      // put은 dispatch 동일
      type: SIGN_UP_SUCCESS
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: SIGN_UP_FAILURE,
      error: e
    });
  }
}

//회원가입 API
function signUpAPI(signUpData) {
  return axios.post("/user/", signUpData);
}

//회원가입 watch
function* watchSignUp() {
  yield takeEvery(SIGN_UP_REQUEST, signUp);
}

//로그아웃
function* logOut(action) {
  try {
    // yield call(logOutAPI);
    yield call(logOutAPI);
    yield put({
      // put은 dispatch 동일
      type: LOG_OUT_SUCCESS
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: LOG_OUT_FAILURE,
      error: e
    });
  }
}

//로그아웃 API
function logOutAPI(signUpData) {
  // 서버에 요청을 보내는 부분
  return axios.post(
    "/user/logout",
    {},
    {
      withCredentials: true
    }
  );
}

//로그아웃 watch
function* watchlogOut() {
  yield takeEvery(LOG_OUT_REQUEST, logOut);
}

function loadUserAPI() {
  // 서버에 요청을 보내는 부분
  return axios.get("/user/", {
    withCredentials: true
  });
}

function* loadUser() {
  try {
    // yield call(loadUserAPI);
    const result = yield call(loadUserAPI);
    yield put({
      // put은 dispatch 동일
      type: LOAD_USER_SUCCESS,
      data: result.data
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: LOAD_USER_FAILURE,
      error: e
    });
  }
}

function* watchLoadUser() {
  yield takeEvery(LOAD_USER_REQUEST, loadUser);
}

//여기가 시작점
//yield 는 중단점이다.
//take는 중단점 실행을 나타내는 트리거 같은 놈인듯
//put은 사가의 디스패치 라고 볼수 있다
//call은 동기요청 fork는 비동기 요청
export default function* userSaga() {
  yield all([
    fork(watchSignUp),
    fork(watchLogIn),
    fork(watchLoadUser),
    fork(watchlogOut)
  ]);
  // console.log('aaaa');
  // yield helloSaga();
}
