import {
  all,
  fork,
  takeLatest,
  takeEvery,
  call,
  put,
  take,
  delay
} from "redux-saga/effects";
import axios from "axios";
import {
  LOG_IN_REQUEST,
  LOG_IN_SUCCESS,
  LOG_IN_FAILURE,
  SIGN_UP_REQUEST,
  SIGN_UP_FAILURE,
  SIGN_UP_SUCCESS
} from "../reducers/user";

axios.defaults.baseURL = "http://localhost:8080/api";

function loginAPI(loginData) {
  //서버에 요청
  return axios.post("/user/login", loginData, {
    withCredentials: true
  });
}

//로그인
function* login(action) {
  try {
    const result = yield call(loginAPI, action.data); //서버 응답
    yield put({
      // put은 dispatch 동일
      type: LOG_IN_SUCCESS,
      data: result.data
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: LOG_IN_FAILURE
    });
  }
}
//watch > 로그인
function* watchLogin() {
  yield takeEvery(LOG_IN_REQUEST, login);
}

function signUpAPI(signUpData) {
  return axios.post("/user/", signUpData);
}

//회원가입
function* signUp(action) {
  try {
    // yield call(signUpAPI);
    //yield delay(2000);
    //throw new Error('에러에러에러');
    yield call(signUpAPI, action.data);
    yield put({
      // put은 dispatch 동일
      type: SIGN_UP_SUCCESS
    });
  } catch (e) {
    // loginAPI 실패
    console.error(e);
    yield put({
      type: SIGN_UP_FAILURE,
      error: e
    });
  }
}
//watch > 회원가입
function* watchSignUp() {
  yield takeEvery(SIGN_UP_REQUEST, signUp);
}

//여기가 시작점
//yield 는 중단점이다.
//take는 중단점 실행을 나타내는 트리거 같은 놈인듯
//put은 사가의 디스패치 라고 볼수 있다
//call은 동기요청 fork는 비동기 요청
export default function* userSaga() {
  yield all([
    fork(watchSignUp),
    fork(watchLogin),
    fork(watchLoadUser),
    fork(watchSignUp)
  ]);
  // console.log('aaaa');
  // yield helloSaga();
}
