import React, {useState, useCallback} from 'react';
import PropTypes from 'prop-types';
import { Icon } from 'antd';
import ImageZoom from './ImageZoom'

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZoom] = useState(false);
  const onZoom = useCallback(() => {
    setShowImagesZoom(true);
  }, []);
  if(images.length === 1) {
    return (
      <>
        <img src={`http://localhost:3065/${images[0].src}`} onClick={onZoom} />
        {showImagesZoom && <ImageZoom images={ images } />}
      </>
    );
  }
  if( images.length === 2) {
    return (
        <div>
            <img src={`http://localhost:3065/${images[0].src}`} width="50%"></img>
            <img src={`http://localhost:3065/${images[1].src}`} width="50%"></img>
        </div>
    );
  }
  return (
    <div>ccㅇㄹ
        <img src={`http://localhost:3065/${images[0].src}`} width="50%"></img>
        <div style={{display:'inline-block', width:'50%', textAlign:'center', verticalAlign:'middle'}}>
            <Icon type="plus" />
            <br />
            {images.length - 1}
            개의 사진 더 보기
        </div>
    </div>
  );
};

PostImages.propTypes = {
    images : PropTypes.arrayOf(PropTypes.shape({
        src:PropTypes.string,
    })).isRequired,
};

export default PostImages;
