import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Col, Input, Menu, Row, Card, Avatar, Button, Form } from 'antd';

const dummy = {
  nickname: '서동열',
  Post: [],
  Followings: [],
  Followers: [],
  isLoggedIn: false,
};

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
        <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
        <Menu.Item key="mail">
          <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
        </Menu.Item>
      </Menu>
      <Row gutter={8}>
        <Col xs={24} md={6}>
          <Card
            actions={[
              <div key="twit">짹짹<br />{dummy.Post.length}</div>,
              <div key="following">팔로잉<br />{dummy.Followings.length}</div>,
              <div key="follower">팔로워<br />{dummy.Followers.length}</div>,
            ]}
          >
            <Card.Meta
              avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
              title={dummy.nickname}
            />
          </Card>
          <Form>
            <div>
              <label htmlFor="user-id">아이디</label>
              <br />
              <Input name="user-id" required ></Input>
            </div>
            <div>
              <label htmlFor="user-password">비밀번호</label>
              <br />
              <Input name="user-password" type="password" required value={password} />
            </div>
            <div>
              <Button type="primary" htmlType="submit" loading={false}>로그인</Button>
              <Link href="/signup"><a><Button>회원가입</Button></a></Link>
            </div>
          </Form>
        </Col>
        <Col xs={24} md={12}>
          {children}
        </Col>
        <Col xs={24} md={6}>
          <Link href="https://www.zerocho.com" ><a target="_blank">Made by ZeroCho</a></Link>
        </Col>
      </Row>
    </div >
  );
};

AppLayout.propTypes = {
  children: PropTypes.node,
};

export default AppLayout;
